 //referencia del hmtml que hay que creer desde js

//funcion que crea el html de los productos
function renderproducts(arr){
    for(product of productlist){
        //constantes creando elementos html desde el js
        const productcard=document.createElement(`div`);
        const imgproduct=document.createElement(`img`);
        const prodcutinfo=document.createElement(`div`);
        const prodcutinfoDIV=document.createElement(`div`);
        const precioproductinfo=document.createElement(`p`);
        const nombreproductinfo=document.createElement(`p`);
        const productinfofigure=document.createElement(`figure`);
        const imagefigure=document.createElement(`img`);
    
        imgproduct.addEventListener(`click`,opendetailproduct2);
        //asignar clase al product info
        prodcutinfo.classList.add(`product-info`)
        //cambiar el src de img product por el de nuestra imagen en el array
        imgproduct.setAttribute(`src`,product.imagen);
        //agregar clase
        productcard.classList.add(`product-card`);
        // <p> adentro de los p se le asigna el nombre y el precio que nuestro for manda recorriendo el array
        precioproductinfo.innerText=`$` + product.precio;
        nombreproductinfo.innerText=product.name;
        // este set attribute del boton del carro
        imagefigure.setAttribute(`src`,`./icons/bt_add_to_cart.svg`);
        //agregando el img del producto
        productcard.appendChild(imgproduct);
        //agregando img dentro del figure
        productinfofigure.appendChild(imagefigure);
        //agregando los p dentro del div
        prodcutinfoDIV.appendChild(precioproductinfo);
        prodcutinfoDIV.appendChild(nombreproductinfo);
        //agregar div y figure dentro del product info
        prodcutinfo.appendChild(prodcutinfoDIV);
        prodcutinfo.appendChild(productinfofigure);
        //agregar product info al product card
        productcard.appendChild(prodcutinfo);
        // agregando  product info a productcard que se encuentra en el html
        cardContainer.appendChild(productcard);
    
    }
    
}
//se llama a la funcion para que se ejecute
renderproducts(productlist);
